
public class Exercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int valor1 = 50;
		int valor2 = 90;
		
		int subtração = valor1 - valor2;
		int soma = valor1 + valor2;
		double divisão = valor1 / valor2;
		int mutiplicação = valor1 * valor2;
		
	System.out.println("A soma é:  "+soma);
	System.out.println("A Divisão é:  "+divisão);
	System.out.println("A Subtração é:  "+subtração);
	System.out.println("A Mutiplicação é:  "+mutiplicação);
		

	}

}
